<?php/** * @file views-view-unformatted--portfolio-slideshow-view--page.tpl.php * Default simple view template to display a list of rows. * * @ingroup views_templates */ drupal_add_css(drupal_get_path('theme', 'glossy') .'/css/portfolio-slideshow.css', array('group' => CSS_THEME));// add needed scripts and css files.$path = libraries_get_path('wmu-slider');drupal_add_js($path .'/jquery.wmu-slider.min.js', array('group' => JS_THEME));?><?php if (!empty($title)): ?>  <h3><?php print $title; ?></h3><?php endif; ?><?php foreach ($rows as $id => $row): ?>  <div class="<?php print $classes_array[$id]; ?> preload-container">    <?php print $row; ?>  </div><?php endforeach; ?>